const pos = { x: 1, y: 1 }

function clamp (n) {
  if (n < 0) return 0
  if (n > 2) return 2
  return n
}

function step (dir) {
  if (dir === 'L') pos.x = clamp(pos.x - 1)
  else if (dir === 'R') pos.x = clamp(pos.x + 1)
  else if (dir === 'U') pos.y = clamp(pos.y - 1)
  else if (dir === 'D') pos.y = clamp(pos.y + 1)
  else if (dir === '\n') console.log(num(pos))
  else throw new Error('wat ' + dir)
}

function num ({ x, y }) {
  return 1 + x + (3 * y)
}

